mapper.select:
select * from product 
where 1=1

	and cno=#{cno}


	and pname like '%'||#{searchData}||'%'


	and price >= #{lowValue}


	and price <= #{highValue}


	order by sales desc


	order by pdate desc


	order by price


	order by price desc

select count(*) from product
where 1=1

	and cno=#{cno}
